"use client";

import { useState, useRef, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Music, VolumeX } from "lucide-react";

export default function MusicToggle() {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  useEffect(() => {
    // We can't include an actual audio file, so we'll create a silent one programmatically
    // to make the controls work without causing a 404 error.
    // In a real app, you would replace the src with your audio file URL.
    const silentAudio = "data:audio/mp3;base64,-/";
    audioRef.current = new Audio(silentAudio);

    const handleEnded = () => setIsPlaying(false);
    const currentAudioRef = audioRef.current;
    currentAudioRef.addEventListener("ended", handleEnded);

    return () => {
      currentAudioRef.removeEventListener("ended", handleEnded);
    };
  }, []);

  const toggleMusic = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play().catch(error => {
          console.error("Audio playback failed:", error);
          setIsPlaying(false);
        });
      }
      setIsPlaying(!isPlaying);
    }
  };

  return (
    <div className="fixed top-4 right-4 z-50">
      <Button
        variant="outline"
        size="icon"
        onClick={toggleMusic}
        className="rounded-full bg-white/50 backdrop-blur-sm border-primary/20 hover:bg-primary/10"
      >
        {isPlaying ? (
          <Music className="h-5 w-5 text-primary" />
        ) : (
          <VolumeX className="h-5 w-5 text-primary/70" />
        )}
        <span className="sr-only">Toggle Music</span>
      </Button>
    </div>
  );
}
