"use client";

import { useState, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Music, VolumeX } from "lucide-react";

export default function MusicToggle() {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef<HTMLAudioElement | null>(null);

  // In a real app, you would replace this with your audio file URL.
  // This is a silent audio clip to make the controls work without a real file.
  const audioSrc = "data:audio/mp3;base64,-/";

  const toggleMusic = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
        setIsPlaying(false);
      } else {
        audioRef.current.play().catch(error => {
          console.error("Audio playback failed:", error);
          setIsPlaying(false);
        });
        setIsPlaying(true);
      }
    }
  };

  return (
    <>
      {/* This audio element is hidden and controlled by the button */}
      <audio
        ref={audioRef}
        src={audioSrc}
        onEnded={() => setIsPlaying(false)}
        preload="auto"
      />
      <div className="fixed top-4 right-4 z-50">
        <Button
          variant="outline"
          size="icon"
          onClick={toggleMusic}
          className="rounded-full bg-white/50 backdrop-blur-sm border-primary/20 hover:bg-primary/10"
        >
          {isPlaying ? (
            <Music className="h-5 w-5 text-primary" />
          ) : (
            <VolumeX className="h-5 w-5 text-primary/70" />
          )}
          <span className="sr-only">Toggle Music</span>
        </Button>
      </div>
    </>
  );
}
